package JavaCore;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Addition of 2 int data type
		int a = 10;
		int b = 30;
		int c = a + b;
		System.out.println(c);
		
		//Subtraction of 2 double data type
		double x = 5.8;
		double y = 9.4;
		double z = x - y;
		System.out.println(z);
		
		//Multiplication of 2 shot data type
		
		short e = 2;
		short j = 3;
		short k = (short) (e * j);
		System.out.println(k);
		
		//Duivision of 2 float data type 
		
		float flot1 = 350.87f;
		float flot2 = 10.6f;
		float flot3 = flot1 / flot2;
		System.out.println(flot3);

	}

}
